        return new Source("{% extends 'login.html.twig' %}

{% block title %}Reset your password{% endblock %}

{% block body %}
    {% for flashError in app.flashes('reset_password_error') %}
        <div class=\"alert alert-danger\" role=\"alert\">{{ flashError }}</div>
    {% endfor %}
    <h4>Réinitialisez votre mot de passe</h4>
    <hr>
    {{ form_start(requestForm) }}
        {{ form_row(requestForm.email) }}
        <div>
            <small>
                Entrez votre adresse e-mail et nous vous enverrons un
                lien pour réinitialiser votre mot de passe.
            </small>
        </div>
    <br>
        <button class=\"btn btn-primary\">Récuperer mon mot de passe</button>
    {{ form_end(requestForm) }}
    {% block loginFooter %}
    {% endblock %}
{% endblock %}", "reset_password/request.html.twig", "C:\\wamp64\\www\\TourismWeb1\\TourismWeb\\templates\\reset_password\\request.html.twig");
